#finds and outputs positions in genome where pattern is found (starting on 0)
pattern = "GCTAATCGC"
genome = ""
positions = []

for i in range(len(genome)-len(pattern)+1):
	if pattern == genome[i:i+len(pattern)]:
		positions.append(i)

tempW = open('output_BA1D.txt', 'w')
for pos in positions:
	tempW.write(str(pos) + " ")
tempW.close()
		